package com.example.smallwhite;/** * @author: yangqiang * @create: 2020-04-09 11:21 * * 平常开发中有可能需要实现在项目启动后执行的功能。 * SpringBoot提供的一种简单的实现方案就是添加一个model并实现CommandLineRunner接口。 * 实现功能的代码放在实现的run方法中 */import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.core.annotation.Order;import org.springframework.stereotype.Component;@Component@Order(1)public class OutputPropsRunner implements CommandLineRunner {    private static final Logger LOGGER = LoggerFactory.getLogger(OutputPropsRunner.class);    @Autowired    private TestVo readByPropertySourceAndValue;    @Override    public void run(String... args) throws Exception {        LOGGER.info(readByPropertySourceAndValue.toString());    }}